mapper.select:
SELECT ds.id ,u.caps_name ,(select name from code_data where group_key='DEPT' and code=u.dept_cd and use_yn='Y') as dept_name ,st_dt ,week_name ,holiday_yn ,convert(varchar(5),go_tm,114) as go_tm ,convert(varchar(5),out_tm,114) as out_tm ,convert(varchar(5),late_tm,114) as late_tm ,convert(varchar(5),exp_out_tm,114) as exp_out_tm ,cal_work_tm_min ,work_tm_min ,st_leave ,st_hl_leave ,st_offcial ,st_short_late ,st_long_late ,st_fail_work_tm ,st_absence ,st_adjust ,memo ,data_error_yn ,(select caps_name from ks_user where id=ds.mdfy_id) as mdfy_id ,ds.mdfy_dt FROM ks_daily_stat ds, ks_user u where ds.id=u.id and st_dm= #{searchDM}
and ds.id=#{searchUser}
and u.dept_cd=#{searchDept}

mapper.update:
update ks_daily_stat
set st_adjust=#{stAdjust}, memo=#{memo},mdfy_id=#{mdfyId}, mdfy_dt=getdate()
where id=#{id} and st_dt=#{stDt}

mapper.select:
select id, caps_name, dept_cd, dept_name,availCount,usedLeave,usedHlLeave , (isnull(usedLeave,0) + isnull(usedHlLeave,0)) as usedCount ,currReplace,subShort,subLong,subFailTm,subAbsence ,subLate,hire_dt,start_dt,end_dt ,(subLate+subFailTm+subAbsence) as subCount ,(availCount-(isnull(usedLeave,0) + isnull(usedHlLeave,0))-(subLate+subFailTm+subAbsence)) as currCount
from ( select u.id, u.auth_cd,u.state_cd,u.caps_id,u.caps_name,u.hire_dt ,a.avail_count as availCount ,a.start_dt,a.end_dt ,u.dept_cd ,(select name from code_data where group_key='DEPT' and code=u.dept_cd and use_yn='Y') as dept_name ,ISNULL((select count(*) from ks_leave where crtd_id=u.id and offcial='N' and le_dt between a.start_dt and a.end_dt group by crtd_id),0) as usedLeave ,ISNULL((select count(distinct hl_dt)*0.5from ks_half_leave where crtd_id=u.id and offcial='N' and hl_dt between a.start_dt and a.end_dt group by crtd_id),0) as usedHlLeave ,kdr.month_replace_count-ISNULL(d.currReplace,0) as currReplace ,ds.subShort ,ds.subLong ,ds.subFailTm ,ds.subAbsence ,ds.short_late_count ,ds.long_late_count ,isnull(FLOOR((1/convert(float, ds.short_late_count)*ds.subShort)+(1/convert(float, ds.long_late_count)*ds.subLong)),0) as subLate from ks_user u left outer join ks_auto_annual a on u.id=a.id left outer join (select kr.crtd_id,count(distinct repl_dt) as currReplace from ks_replace kr where convert(varchar(7), repl_dt, 120) =convert(varchar(7), getdate(), 120) group by kr.crtd_id) d on u.id=d.crtd_id left outer join ( select kds.id ,sum(kds.st_short_late) as subShort ,sum(kds.st_long_late) as subLong ,sum(kds.st_fail_work_tm) as subFailTm ,sum(kds.st_absence) as subAbsence ,min(kyr.short_late_count) as short_late_count ,min(kyr.long_late_count) as long_late_count from ks_daily_stat kds left outer join ks_auto_annual a on kds.id=a.id , ks_yearly_rule kyr where kds.st_dt between a.start_dt and a.end_dt and kds.st_adjust='N' and kyr.apply_year=YEAR(GETDATE()) and kds.st_year=kyr.apply_year group by kds.id ) ds on u.id=ds.id ,ks_daily_rule kdr where u.auth_cd <> '003' and u.state_cd = '001' and u.id=#{id} and CONVERT(CHAR(10), getdate(), 121) between a.start_dt and a.end_dt
) k

select id, caps_name, dept_cd, dept_name,availCount,usedLeave,usedHlLeave , (isnull(usedLeave,0) + isnull(usedHlLeave,0)) as usedCount ,currReplace,subShort,subLong,subFailTm,subAbsence ,subLate,hire_dt,start_dt,end_dt ,(subLate+subFailTm+subAbsence) as subCount ,(availCount-(isnull(usedLeave,0) + isnull(usedHlLeave,0))-(subLate+subFailTm+subAbsence)) as currCount
from ( select u.id, u.auth_cd,u.state_cd,u.caps_id,u.caps_name,u.hire_dt ,a.avail_count as availCount ,a.start_dt,a.end_dt ,u.dept_cd ,(select name from code_data where group_key='DEPT' and code=u.dept_cd and use_yn='Y') as dept_name ,ISNULL((select count(*) from ks_leave where crtd_id=u.id and offcial='N' and le_dt between a.start_dt and a.end_dt group by crtd_id),0) as usedLeave ,ISNULL((select count(distinct hl_dt)*0.5from ks_half_leave where crtd_id=u.id and offcial='N' and hl_dt between a.start_dt and a.end_dt group by crtd_id),0) as usedHlLeave ,kdr.month_replace_count-ISNULL(d.currReplace,0) as currReplace ,ds.subShort ,ds.subLong ,ds.subFailTm ,ds.subAbsence ,ds.short_late_count ,ds.long_late_count ,isnull(FLOOR((1/convert(float, ds.short_late_count)*ds.subShort)+(1/convert(float, ds.long_late_count)*ds.subLong)),0) as subLate from ks_user u left outer join ks_auto_annual a on u.id=a.id left outer join (select kr.crtd_id,count(distinct repl_dt) as currReplace from ks_replace kr where convert(varchar(7), repl_dt, 120) =convert(varchar(7), getdate(), 120) group by kr.crtd_id) d on u.id=d.crtd_id left outer join ( select kds.id ,sum(kds.st_short_late) as subShort ,sum(kds.st_long_late) as subLong ,sum(kds.st_fail_work_tm) as subFailTm ,sum(kds.st_absence) as subAbsence ,min(kyr.short_late_count) as short_late_count ,min(kyr.long_late_count) as long_late_count from ks_daily_stat kds left outer join ks_auto_annual a on kds.id=a.id , ks_yearly_rule kyr where kds.st_dt between a.start_dt and a.end_dt and kds.st_adjust='N' and kyr.apply_year=YEAR(GETDATE()) and kds.st_year=kyr.apply_year group by kds.id ) ds on u.id=ds.id ,ks_daily_rule kdr where u.auth_cd <> '003' and u.state_cd = '001' and CONVERT(CHAR(10), getdate(), 121) between a.start_dt and a.end_dt
and u.id=#{searchUser}
and u.dept_cd=#{searchDept}
) k

select u.id ,u.caps_name ,u.dash_state ,(select name from code_data where group_key='DEPT' and code=u.dept_cd and use_yn='Y') as dept_name ,isnull(klp.year,#{searchYear}) as year ,klp.short_late ,klp.long_late ,klp.org_late_point ,klp.mail_to ,klp.mail_count ,convert(varchar(16),klp.last_mail_send_dt,120) as last_mail_send_dt ,klp.crtd_dt ,klp.mdfy_dt from ks_user u left outer join (select * from ks_late_point where year=#{searchYear}) klp on u.id=klp.id where u.auth_cd <> '003' and u.state_cd='001'
and u.id=#{searchUser}
and u.dept_cd=#{searchDept}

mapper.update:
UPDATE ks_late_point
SET short_late=#{shortLate},long_late=#{longLate},org_late_point=#{orgLatePoint},late_point=#{latePoint},last_mail_send_dt=getdate(),mail_count=#{latePoint},mdfy_dt=getdate()
WHERE id=#{id} and year=#{year}

UPDATE ks_late_point
SET short_late=#{shortLate},long_late=#{longLate},org_late_point=#{orgLatePoint},late_point=#{latePoint},mail_count=#{latePoint},mdfy_dt=getdate()
WHERE id=#{id} and year=#{year}
